import com.danieibazgc.interbank.ui.theme.InterbankGreen
import com.danieibazgc.interbank.ui.theme.InterbankTextGray

@Composable
fun HomeScreen(
    onLogout: () -> Unit = {}
) {
    var selectedTab by remember { mutableStateOf(0) }
    var saldosOcultos by remember { mutableStateOf(false) }

    Scaffold(
        topBar = {
            HomeTopBar(
                saldosOcultos = saldosOcultos,
                onToggleSaldos = { saldosOcultos = !saldosOcultos }
            )
        },
        bottomBar = {
            HomeBottomBar(
                selectedTab = selectedTab,
                onTabSelected = { selectedTab = it }
            )
        },
        floatingActionButton = {
            FloatingActionButton(
                onClick = { /* Acción QR */ },
                containerColor = InterbankGreen,
                contentColor = Color.White,
                modifier = Modifier.size(64.dp)
            ) {
                Icon(
                    imageVector = Icons.Filled.Search,
                    contentDescription = "QR",
                    modifier = Modifier.size(32.dp)
                )
            }
        },
        floatingActionButtonPosition = FabPosition.Center
    ) { paddingValues ->
        Column(
            modifier = Modifier
                .fillMaxSize()
                .background(InterbankGray)
                .padding(paddingValues)
                .verticalScroll(rememberScrollState())
        ) {
            // Header con información de T.C. referencial
            HeaderInfo(saldosOcultos = saldosOcultos)

            Spacer(modifier = Modifier.height(16.dp))

            // Sección de Productos
            ProductsSection(
                saldosOcultos = saldosOcultos,
                onToggleSaldos = { saldosOcultos = !saldosOcultos }
            )

            Spacer(modifier = Modifier.height(24.dp))

            // Sección de Pagos Rápidos
            QuickPaymentsSection()

            Spacer(modifier = Modifier.height(24.dp))

            // Banner de pagos a tiempo
            PaymentsBanner()

            Spacer(modifier = Modifier.height(24.dp))
        }
    }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun HomeTopBar(
    saldosOcultos: Boolean,
    onToggleSaldos: () -> Unit
) {
    TopAppBar(
        title = {
            Row(
                verticalAlignment = Alignment.CenterVertically,
                modifier = Modifier.fillMaxWidth()
            ) {
                // Logo
                Box(
                    modifier = Modifier
                        .size(32.dp)
                        .background(
                            color = InterbankBlue,
                            shape = RoundedCornerShape(4.dp)
                        )
                )
                Spacer(modifier = Modifier.width(8.dp))
                Text(
                    text = "Interbank",
                    fontSize = 20.sp,
                    fontWeight = FontWeight.Bold,
                    color = InterbankGreen
                )
            }
        },
        actions = {
            IconButton(onClick = { /* Notificaciones */ }) {
                Icon(
                    imageVector = Icons.Outlined.Notifications,
                    contentDescription = "Notificaciones",
                    tint = InterbankGreen
                )
            }
            IconButton(onClick = { /* Menú */ }) {
                Icon(
                    imageVector = Icons.Filled.Menu,
                    contentDescription = "Menú",
                    tint = InterbankGreen
                )
            }
        },
        colors = TopAppBarDefaults.topAppBarColors(
            containerColor = Color.White
        )
    )
}

@Composable
fun HeaderInfo(saldosOcultos: Boolean) {
    Card(
        modifier = Modifier
            .fillMaxWidth()
            .padding(horizontal = 16.dp),
        colors = CardDefaults.cardColors(containerColor = Color.White),
        elevation = CardDefaults.cardElevation(defaultElevation = 2.dp)
    ) {
        Column(
            modifier = Modifier.padding(16.dp)
        ) {
            Text(
                text = "T.C. referencial: Compra: S/3.930 Venta: S/3.980",
                fontSize = 12.sp,
                color = InterbankTextGray
            )
        }
    }
}

@Composable
fun ProductsSection(
    saldosOcultos: Boolean,
    onToggleSaldos: () -> Unit
) {
    Column(
        modifier = Modifier.padding(horizontal = 16.dp)
    ) {
        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.SpaceBetween,
            verticalAlignment = Alignment.CenterVertically
        ) {
            Text(
                text = "Productos",
                fontSize = 18.sp,
                fontWeight = FontWeight.Bold,
                color = Color.Black
            )
            TextButton(
                onClick = onToggleSaldos,
                colors = ButtonDefaults.textButtonColors(
                    contentColor = InterbankGreen
                )
            ) {
                Text(
                    text = if (saldosOcultos) "Mostrar saldos" else "Ocultar saldos",
                    fontSize = 12.sp,
                    fontWeight = FontWeight.Medium,
                    color = InterbankGreen
                )
            }
        }

        Spacer(modifier = Modifier.height(12.dp))

        // Cuenta Simple Soles
        ProductCard(
            title = "Cuenta Simple Soles",
            amount = if (saldosOcultos) "****" else "S/ 1,200.00",
            subtitle = "Saldo disponible",
            backgroundColor = Color(0xFFE8F5F1),
            icon = Icons.Filled.AccountBox
        )

        Spacer(modifier = Modifier.height(12.dp))

        // Tarjeta Visa Premia
        ProductCard(
            title = "Tarjeta Visa Premia",
            amount = if (saldosOcultos) "****" else "S/ 25,000.00",
            subtitle = "Línea de crédito disponible",
            backgroundColor = Color(0xFFE8F5F1),
            icon = Icons.Filled.Star
        )

        Spacer(modifier = Modifier.height(12.dp))

        // Millas Benefit
        Card(
            modifier = Modifier.fillMaxWidth(),
            colors = CardDefaults.cardColors(containerColor = Color(0xFFE8F5F1)),
            shape = RoundedCornerShape(12.dp)
        ) {
            Row(
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(16.dp),
                horizontalArrangement = Arrangement.SpaceBetween,
                verticalAlignment = Alignment.CenterVertically
            ) {
                Row(
                    verticalAlignment = Alignment.CenterVertically
                ) {
                    Box(
                        modifier = Modifier
                            .size(48.dp)
                            .background(
                                color = InterbankBlue,
                                shape = RoundedCornerShape(8.dp)
                            ),
                        contentAlignment = Alignment.Center
                    ) {
                        Icon(
                            imageVector = Icons.Filled.Star,
                            contentDescription = null,
                            tint = Color.White,
                            modifier = Modifier.size(28.dp)
                        )
                    }
                    Spacer(modifier = Modifier.width(12.dp))
                    Column {
                        Text(
                            text = "Millas Benefit",
                            fontSize = 16.sp,
                            fontWeight = FontWeight.SemiBold,
                            color = Color.Black
                        )
                        Text(
                            text = "¡Conócelas!",
                            fontSize = 14.sp,
                            color = InterbankGreen,
                            fontWeight = FontWeight.Medium
                        )
                    }
                }
            }
        }
    }
}

@Composable
fun ProductCard(
    title: String,
    amount: String,
    subtitle: String,
    backgroundColor: Color,
    icon: ImageVector
) {
    Card(
        modifier = Modifier.fillMaxWidth(),
        colors = CardDefaults.cardColors(containerColor = backgroundColor),
        shape = RoundedCornerShape(12.dp)
    ) {
        Row(
            modifier = Modifier
                .fillMaxWidth()
                .padding(16.dp),
            horizontalArrangement = Arrangement.SpaceBetween,
            verticalAlignment = Alignment.CenterVertically
        ) {
            Row(
                verticalAlignment = Alignment.CenterVertically
            ) {
                Box(
                    modifier = Modifier
                        .size(48.dp)
                        .background(
                            color = Color(0xFFFF69B4).copy(alpha = 0.3f),
                            shape = RoundedCornerShape(8.dp)
                        ),
                    contentAlignment = Alignment.Center
                ) {
                    Icon(
                        imageVector = icon,
                        contentDescription = null,
                        tint = Color(0xFFFF1493),
                        modifier = Modifier.size(28.dp)
                    )
                }
                Spacer(modifier = Modifier.width(12.dp))
                Column {
                    Text(
                        text = title,
                        fontSize = 16.sp,
                        fontWeight = FontWeight.SemiBold,
                        color = Color.Black
                    )
                    Text(
                        text = amount,
                        fontSize = 18.sp,
                        fontWeight = FontWeight.Bold,
                        color = Color.Black
                    )
                    Text(
                        text = subtitle,
                        fontSize = 12.sp,
                        color = InterbankTextGray
                    )
                }
            }
        }
    }
}

