}

@Composable
fun HomeBottomBar(
    selectedTab: Int,
    onTabSelected: (Int) -> Unit
) {
    NavigationBar(
        containerColor = Color.White,
        contentColor = InterbankGreen
    ) {
        NavigationBarItem(
            icon = {
                Icon(
                    imageVector = Icons.Filled.Home,
                    contentDescription = "Inicio"
                )
            },
            label = { Text("Inicio", fontSize = 10.sp) },
            selected = selectedTab == 0,
            onClick = { onTabSelected(0) },
            colors = NavigationBarItemDefaults.colors(
                selectedIconColor = InterbankGreen,
                selectedTextColor = InterbankGreen,
                unselectedIconColor = InterbankTextGray,
                unselectedTextColor = InterbankTextGray,
                indicatorColor = InterbankGray
            )
        )

        NavigationBarItem(
            icon = {
                Icon(
                    imageVector = Icons.Filled.Settings,
                    contentDescription = "Operaciones"
                )
            },
            label = { Text("Operaciones", fontSize = 10.sp) },
            selected = selectedTab == 1,
            onClick = { onTabSelected(1) },
            colors = NavigationBarItemDefaults.colors(
                selectedIconColor = InterbankGreen,
                selectedTextColor = InterbankGreen,
                unselectedIconColor = InterbankTextGray,
                unselectedTextColor = InterbankTextGray,
                indicatorColor = InterbankGray
            )
        )

        // Espacio para el FAB
        NavigationBarItem(
            icon = { },
            label = { },
            selected = false,
            onClick = { },
            enabled = false
        )

        NavigationBarItem(
            icon = {
                Icon(
                    imageVector = Icons.Filled.AccountCircle,
                    contentDescription = "Mis Finanzas"
                )
            },
            label = { Text("Mis Finanzas", fontSize = 10.sp) },
            selected = selectedTab == 3,
            onClick = { onTabSelected(3) },
            colors = NavigationBarItemDefaults.colors(
                selectedIconColor = InterbankGreen,
                selectedTextColor = InterbankGreen,
                unselectedIconColor = InterbankTextGray,
                unselectedTextColor = InterbankTextGray,
                indicatorColor = InterbankGray
            )
        )

        NavigationBarItem(
            icon = {
                Icon(
                    imageVector = Icons.Filled.MoreVert,
                    contentDescription = "Para ti"
                )
            },
            label = { Text("Para ti", fontSize = 10.sp) },
            selected = selectedTab == 4,
            onClick = { onTabSelected(4) },
            colors = NavigationBarItemDefaults.colors(
                selectedIconColor = InterbankGreen,
                selectedTextColor = InterbankGreen,
                unselectedIconColor = InterbankTextGray,
                unselectedTextColor = InterbankTextGray,
                indicatorColor = InterbankGray
            )
        )
    }
}

package com.danieibazgc.interbank.ui.screens

import androidx.compose.animation.core.Animatable
import androidx.compose.animation.core.tween
import androidx.compose.foundation.Image
import androidx.compose.foundation.background
import androidx.compose.foundation.layout.Box
import androidx.compose.foundation.layout.fillMaxSize
import androidx.compose.foundation.layout.size
import androidx.compose.runtime.Composable
import androidx.compose.runtime.LaunchedEffect
import androidx.compose.runtime.remember
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.draw.alpha
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.res.painterResource
import androidx.compose.ui.unit.dp
import com.danieibazgc.interbank.R
import kotlinx.coroutines.delay

@Composable
fun SplashScreen(
    onNavigateToLogin: () -> Unit
) {
    // Animación de fade-in para el logo
    val alpha = remember { Animatable(0f) }

    LaunchedEffect(key1 = true) {
        // Animar el fade-in
        alpha.animateTo(
            targetValue = 1f,
            animationSpec = tween(durationMillis = 800)
        )

        // Mantener el splash visible por 2.5 segundos en total
        delay(2500)

        // Navegar al login
        onNavigateToLogin()
    }

    Box(
        modifier = Modifier
            .fillMaxSize()
            .background(Color.White),
        contentAlignment = Alignment.Center
    ) {
        Image(
            painter = painterResource(id = R.drawable.ibk),
            contentDescription = "Logo Interbank",
            modifier = Modifier
                .size(200.dp)
                .alpha(alpha.value)
        )
    }
}

